import React, { useState } from 'react'
import { Link } from 'react-router-dom'
import { useNavigate } from "react-router";

export default function Add() {

  const [form, setForm] = useState({
    name:"",
    date:"",
    table:"",
});

const navigate = useNavigate();

 // These methods will update the state properties.
 function updateForm(value) {
  return setForm((prev) => {
    return { ...prev, ...value };
  });
}

  // This function will handle the submission.
  async function onSubmit(e) {
    e.preventDefault();

    // When a post request is sent to the create url, we'll add a new record to the database.
    const newWork = { ...form };

    await fetch("http://localhost:4000/workout/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newWork),
    })
    .catch(error => {
      window.alert(error);
      return;
    });

    setForm({ name: "", position: "", level: "" });
    navigate("/Workout/View");
  }

    return (
       <div className="panel page">
            <header className="panel-heading">
          <div className='row p-2'>
            <div className='col-6'>
              <h3 className="panel-title">Add Workout</h3>
            </div>
            <div className='col-6 text-right'>
            </div>
          </div>
        </header>
            <div className="panel-body container-fluid">
                <div className="example col-8">
                    <form>
                            <div className="form-group row">
                                <label className="col-md-3 col-form-label">Full Name: </label>
                                <div className="col-md-9">
                                <input type="text" className="form-control" name="name" autoComplete="off"
                                   value={form.name}
                                   onChange={(e) => updateForm({ fullName: e.target.value })}
                                />
                                </div>
                            </div>
                           
                            <div className="form-group row">
                                <label className="col-md-3 col-form-label">Date Of Birth: </label>
                                <div className="col-md-9">
                                <div class="input-group input-group-icon">
                                <input type="date" className="form-control" placeholder="Email" />
                                    <span class="input-group-addon">
                                    <span class="icon wb-calendar" aria-hidden="true"></span>
                                    </span>
                                </div>
                                </div>
                            </div>

                            <div className="form-group row text-right">
                                    <div className="col-md-12">
                                    <button type="submit" className="btn btn-primary mr-1">Create Workout</button>
                                    <button type="reset" className="btn btn-danger">Clear</button>
                                    </div>
                            </div>
                    </form>
                </div>
            </div>
        </div>
    )
}
